mapper.update:
UPDATE CRD_MEM 
SET PASSWD = #{passwd} , NAME = #{name}, EMAIL = #{email}
, ZIPCODE = #{zipcode}, ADDRESS = #{address}, ADDRESS_DETAIL = #{address_detail}
, TEL = #{tel}, BIRTH_DATE = #{birth_date}, JOB = #{job}
WHERE MEM_ID = #{mem_id}

mapper.select:
SELECT COUNT(*) 
FROM CRD_MEM
WHERE EMAIL = #{email}

SELECT MEM_ID 
FROM CRD_MEM
WHERE EMAIL = #{email}
